package jp.co.topgate.asada.web;

import java.io.IOException;
import java.util.Scanner;

/*
 * This Java source file was generated by the Gradle 'init' task.
 */
public class App {
    private static final String START_NUM = "1";
    private static final String STOP_NUM = "2";
    private static final String END_NUM = "3";

    public static void main(String[] args) {
        try {
            Server server = new Server();
            String choices;
            Scanner scan = new Scanner(System.in);
            do {
                System.out.println("--------------------");
                System.out.println(START_NUM + ": START");
                System.out.println(STOP_NUM + ": STOP");
                System.out.println(END_NUM + ": END");
                do {
                    System.out.print("please select :");
                    choices = scan.next();
                } while (!(choices.equals(START_NUM) || choices.equals(STOP_NUM) || choices.equals(END_NUM)));
                String msg = doSomething(server, choices);
                System.out.println(msg);
            } while (!choices.equals(END_NUM));
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
    public static String doSomething(Server server, String choices) throws IOException{
        String msg;
        switch (choices) {
            case START_NUM:
                switch (server.getState()) {
                    case TERMINATED:
                        server = new Server();
                    case NEW:
                        server.startServer();
                        msg = "start up http server..";
                        break;
                    case RUNNABLE:
                        msg = "http server is already running..";
                    default:
                        //調べて来ないとしてもjavaは網羅チェックをしていないので意図しているのかもしくは偶然なのか不明になる
                        msg = "http server is wrong state";
                }
                break;
            case STOP_NUM:
                switch (server.getState()) {
                    case NEW:
                    case TERMINATED:
                        msg = "http server is not running..";
                        break;
                    case RUNNABLE:
                        if (server.stopServer()) {
                            msg = "http server stops..";
                        } else {
                            msg = "wait a second, http server is returning a response..";
                        }
                        break;
                    default:
                        msg = "http server is wrong state";
                }
                break;
            case END_NUM:
                if (server.endServer()) {
                    msg = "bye..";
                } else {
                    msg = "wait a second, http server is returning a response..";
                }
                break;
            default:
                //エラー処理予定地
                msg = "エラー";
        }
        return msg;
    }
}
